import React, { useState } from 'react';
import axios from 'axios';
import './Signup.css';

const Signup = () => {
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post('http://localhost:5000/signup?username="deloite"&email="nunesupriya8@gmail.com"&password="Deloite@109"', { username, email, password });
            if (res.data.success) {
                console.log(res.data.message);
                alert('Sign-Up Successful');
            } else {
                console.log(res.data.message);
                alert('Sign-Up Failed: ' + res.data.message);
            }
        } catch (err) {
            if (err.response) {
                console.error('Error response:', err.response.data);
                alert('Sign-Up Failed: ' + err.response.data.message);
            } else if (err.request) {
                console.error('Error request:', err.request);
                alert('Sign-Up Failed: No response from server.');
            } else {
                console.error('Error:', err.message);
                alert('Sign-Up Failed: ' + err.message);
            }
        }
    };

    return (
        <html>
            <body className='body'>
                <form onSubmit={handleSubmit} className='form'>
                    <p className='title'>Signup</p>
                    <input 
                        className='username'
                        type="text" 
                        placeholder="Username" 
                        value={username} 
                        onChange={(e) => setUsername(e.target.value)} 
                    />
                    <input 
                        className='email'
                        type="email" 
                        placeholder="Email" 
                        value={email} 
                        onChange={(e) => setEmail(e.target.value)} 
                    />
                    <input 
                        className='password'
                        type="password" 
                        placeholder="Password" 
                        value={password} 
                        onChange={(e) => setPassword(e.target.value)} 
                    />
                    <button className='button' type="submit">Sign Up</button>
                    <p>already signed up? <a href="/login">Sign in!</a></p>
                </form> 
            </body>
        </html>
    );
};

export default Signup;
